import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  state = {
    response: '',
    //post: '',
    cityA : '',
    cityB : '',
    responseToPost: '',
  };
  componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err));
  }
  callApi = async () => {
    const response = await fetch('/api/hello');
    const body = await response.json();
    if (response.status !== 200) throw Error(body.message);
    return body;
  };
  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/api/world', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({cityA: this.state.cityA,cityB: this.state.cityB}),
    });
    const body = await response.text();
    this.setState({ responseToPost: body });
  };
render() {
    return (
      <div className="App">
        <header className="App-header">
        </header>
        <p>{this.state.response}</p>
        <form onSubmit={this.handleSubmit}>
          <p>
            <strong>Find distance using Haversine formula:</strong>
          </p>
          <label>
            CITY A - 
          <input
            type="text"
            value={this.state.cityA}
            onChange={e => this.setState({ cityA: e.target.value })}
          />
          </label>
          <label>
            CITY B -   
          <input
            type="text"
            value={this.state.cityB}
            onChange={e => this.setState({ cityB: e.target.value })}
          />
          </label>

          <button type="submit">Submit</button>
        </form>
        <p>{this.state.responseToPost}</p>
      </div>
    );
  }
}
export default App;
